import React from 'react'
import PropTypes from 'prop-types'
import { makeStyles } from '@material-ui/core/styles'
import AppBar from '@material-ui/core/AppBar'
import Tabs from '@material-ui/core/Tabs'
import Tab from '@material-ui/core/Tab'
import Box from '@material-ui/core/Box'
import { useSelector } from 'react-redux'
import GoalCard from './GoalCard'
import VerificationPage from '../VerificationGoal'

function TabPanel(props) {
  const { children, value, index, ...other } = props

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          {children}
          {/* <Typography>
            </Typography> */}
        </Box>
      )}
    </div>
  )
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
}

function a11yProps(index) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  }
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}))

export default function GoalsStatusTabs() {
  const classes = useStyles()
  const [value, setValue] = React.useState(0)

  const handleChange = (event, newValue) => {
    setValue(newValue)
  }

  const selector = useSelector((state) => {
    return state.userReducer
  })


  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
          aria-label="scrollable auto tabs example"
        >
          <Tab label="Verify" {...a11yProps(0)} />
          <Tab label="Pending" {...a11yProps(1)} />
          <Tab label="Approved" {...a11yProps(2)} />
          <Tab label="Decline" {...a11yProps(3)} />
        </Tabs>
      </AppBar>
        <>
          <TabPanel value={value} index={0}>
            <VerificationPage />
          </TabPanel>
          <TabPanel value={value} index={1}>
            <GoalCard cardData={selector.pendingGoals} />
          </TabPanel>
          <TabPanel value={value} index={2}>
            <GoalCard cardData={selector.approvedGoals} />
          </TabPanel>
          <TabPanel value={value} index={3}>
            <GoalCard cardData={selector.declineGoals} />
          </TabPanel>
        </>
    </div>
  )
}
